import React from 'react';
import ReactDOM from 'react-dom';


class App extends React.Component {
	constructor(props) { 	
	// constructor is first thing called, used for initializing
		super(props);
		// have to call super and have to pass props;
		// React.Comononent comes with its own constructor, super overrides it so we can use our own constructor
		// error code if don't use super(props)

		this.state = { lat: null };

		window.navigator.geolocation.getCurrentPosition(
			(position) => {
				this.setState({ lat: position.coords.latitude })
			},
			(err) => console.log(err) 
		);
	}

	// React says we have to define render
	render() {
		return <div>Latitude: {this.state.lat}</div>;
	}
}


ReactDOM.render(
	<App />,
	document.querySelector('#root')
);
